import React, {useState, useEffect} from 'react';
import {nanoid} from 'nanoid';
import Todo from './components/Todo';
import Form from './components/Form';
import FilterButton from './components/FilterButton';

function App({data}) {
  const [todos, setTodos] = useState(data);
  const [keyword, setKeyword] = useState('all');

  const filter = (keyword, todos) => {
    switch (keyword) {
      case 'all':
        return todos;
      case 'active':
        return todos.filter(todo => todo.completed === false);
      case 'completed':
        return todos.filter(todo => todo.completed === true);
    }
  }

  const filteredTodos = filter(keyword, todos);

  const addTask = (name) => {
    const id = `todo-${nanoid()}`;
    setTodos([...todos, {id, name, completed: false}]);
  }

  const handleOnCheckChange = (targetTodo) => {
    const newTodos = todos.map(todo => todo.id === targetTodo.id ? {...todo, completed: !todo.completed} : todo);
    console.log(newTodos);
  }

  const handleOnClickDelete = (id) => {
    const newTodos = todos.filter(todo => todo.id !== id);

    setTodos(newTodos);
  }

  const handleOnClickSave = (id, newName) => {
    const newTodos = todos.map(todo => todo.id === id ? {...todo, name: newName} : todo);

    setTodos(newTodos);
    console.log(newTodos);
  }

  const handleOnClickFilter = (name) => {
    setKeyword(name);
  }

  return (
    <div className="todoapp stack-large">
      <h1>HandyTodo</h1>
      <Form addTask={addTask} />
      <div className="filters btn-group stack-exception">
        <FilterButton name="all" pressed="true" onClickFilter={handleOnClickFilter}/>
        <FilterButton name="active" pressed="false" onClickFilter={handleOnClickFilter}/>
        <FilterButton name="completed" pressed="false" onClickFilter={handleOnClickFilter}/>
      </div>
      <h2 id="list-heading">
        {todos.length > 1 ? `${todos.length} tasks remaining` : `${todos.length} task remaining`}
      </h2>
      <ul
        className="todo-list stack-large stack-exception"
        aria-labelledby="list-heading"
      >
        {filteredTodos.map(todo => <Todo key={todo.id} id={todo.id} name={todo.name} completed={todo.completed} onCheckChange={() => handleOnCheckChange(todo)} onClickDelete={handleOnClickDelete} onClickSave={handleOnClickSave} />)}
      </ul>
    </div>
  );
}

export default App;
