import React from 'react';

export default function Todo({id, name, completed, onCheckChange, onClickDelete, onClickEdit, isEditing, onEdit}) {
    return (
        <li className="todo stack-small">
          <div className="c-cb">
            <input id={id} type="checkbox" defaultChecked={completed} onChange={onCheckChange} />
            {isEditting ? (
              <input type="text" value={name} onChange={onEdit} />
            ) : (<label className="todo-label" htmlFor={id}>
              {name}
            </label>)}
          </div>
          <div className="btn-group">
            <button type="button" className="btn" onClick={onClickEdit}>
              {isEditing ? "Save" : "Edit"} <span className="visually-hidden">{name}</span>
            </button>
            <button type="button" className="btn btn__danger" onClick={onClickDelete}>
              Delete <span className="visually-hidden">{name}</span>
            </button>
          </div>
        </li>
    );
}
